mapper.update:
update board set
         ttl = #{ttl}
	   , ctt = #{ctt}
 where bno= #{bno} and subjno = #{subjno}

update board set
	   delyn = 'y'
 where bno= #{bno} and subjno = #{subjno}

mapper.select:
select * 
  	  from (
  	  		select rownum as rnum, a.* 
     		  from (
      				select /*+ index_desc(b BOARD_SUBJNO_BNO_PK) */ b.*, m.niknm
        		   	  from board b join mem m 
          				on b.mno = m.mno
       				 where subjno = #{subjno}
       				   and delyn = 'n'

        		    ) a 

	        where rownum <= #{isp.endRowNum}
	      ) b
	 where rnum >= #{isp.startRowNum}

select count(bno)
  from board
 where subjno = #{subjno}
