mapper.select:
select *
  from inform
 where igubun = #{igubun} AND ino = #{ino}

mapper.delete:
delete 
from inform
where igubun = #{igubun} AND ino = #{ino}

mapper.update:
update inform
set ttl =#{ttl}, ctt = #{ctt}
where igubun = #{igubun} AND ino = #{ino}

mapper.select:
	    select * 
        from (
         select rownum as rnum, a.* 
           from (
               SELECT /*+ index_desc(inform inform_igubun_ino_pk) */ igubun
                      , ino
                      , ttl
                      , ctt
                      , readcnt
                      , regdt
                      , mgid
                 FROM inform 
                WHERE igubun = 'g'

                   where ${searchType} like '%'||#{keyWord}||'%'

               ) a 

             where rownum <= #{endRowNum}
            ) b
       where rnum >= #{startRowNum}

	select * 
        from (
         select rownum as rnum, a.* 
           from (
               SELECT /*+ index_desc(inform inform_igubun_ino_pk) */ igubun
                      , ino
                      , ttl
                      , ctt
                      , readcnt
                      , regdt
                      ,	upfile
                      , mno
                      , mgid
                 FROM inform 
                WHERE igubun = 's'

                   where ${searchType} like '%'||#{keyWord}||'%'

               ) a 

             where rownum <= #{endRowNum}
            ) b
       where rnum >= #{startRowNum}
