mapper.update:
update cts
   set readcnt = readcnt + 1
 where ctscateno=#{ctscateno}
   and ctsno=#{ctsno}

update cts
   set joacnt = joant + 1
 where ctscateno=#{ctscateno}
   and ctsno=#{ctsno}

mapper.insert:
insert into cts(
				ctscateno
			  , ctsno
			  , ttl
			  , ctt
			  , filelk
			  , pt
			  , lat
			  , lng
			  , makedt
			  , mno
				)
		values(
				#{ctscateno}
			  , cts_next(#{ctscateno})
			  , #{ttl}
			  , #{ctt}
			  , #{filelk}
			  , #{pt}
			  , #{lat}
			  , #{lng}
			  , #{makedt}
			  , #{mno}
				)

mapper.update:
update cts
   set ttl = #{ttl}
     , ctt = #{ctt}
     , filelk = #{filelk}
     , pt = #{pt}
     , lat = #{lat}
     , lng = #{lng}
     , makedt = #{makedt}
 where mno = #{mno}
 	 , ctscateno = #{ctscateno}
 	 , ctsno = #{ctsno}

update cts
   set delyn = 'y'
 where ctsno = #{ctsno}
   and ctscateno = #{ctscateno}
