mapper.update:
update cts
   set readcnt = readcnt + 1
 where ctscateno=#{ctscateno}
   and ctsno=#{ctsno}

mapper.insert:

	select cts_next(#{ctscateno}) as ctsno from dual

insert into cts(
				ctscateno
			  , ctsno
			  , ttl
			  , ctt
			  , filelk
			  , pt
			  , lat
			  , lng
			  , mno
				)
		values(
				#{ctscateno}
			  , cts_next(#{ctscateno})
			  , #{ttl}
			  , #{ctt}
			  , #{filelk}
			  , #{pt}
			  , #{lat}
			  , #{lng}
			  , #{mno}
				)

insert into ctstag(
					ctscateno
				  , ctsno
				  , tagno
				  , tagnm
					)
			values
				(
					#{ctscateno}
				  , #{ctsno}
				  , ctstag_next(#{ctscateno},#{ctsno})
				  , #{tagnm}
				  )

mapper.update:
update cts
   set ttl = #{ttl}
     , ctt = #{ctt}
     , filelk = #{filelk}
     , pt = #{pt}
     , lat = #{lat}
     , lng = #{lng}
 where mno = #{mno}
 	 , ctscateno = #{ctscateno}
 	 , ctsno = #{ctsno}

update cts
   set delyn = 'y'
 where ctsno = #{ctsno}
   and ctscateno = #{ctscateno}
   and mno = #{mno}

mapper.select:
select fgubun
  from favorit
 where mno=#{mno}
   and ctscateno=#{ctscateno}
   and ctsno=#{ctsno}

select joacnt
	 , favcnt
  from cts
 where ctscateno=#{ctscateno}
   and ctsno=#{ctsno}

select *
  from favorit
 where mno=#{mno}
   and ctsno=#{ctsno}
   and ctscateno=#{ctscateno}
   and fgubun=#{fgubun}

mapper.insert:
insert into favorit(
					fno
				  , fgubun
				  , ctscateno
				  , ctsno
				  , mno
					)
			values(
					favorit_seq.nextval
				  , #{fgubun}
				  , #{ctscateno}
				  , #{ctsno}
				  , #{mno}
					)

mapper.delete:
delete
  from favorit
 where mno=#{mno}
   and fgubun=#{fgubun}
   and ctscateno=#{ctscateno}
   and ctsno=#{ctsno}

mapper.select:
select datcnt
  from cts
 where ctscateno = #{ctscateno}
   and ctsno = #{ctsno}

mapper.insert:
insert into ctsdat(
					ctscateno
				  , ctsno
				  , ctsdatno
				  , mno
				  , ctt
					)
			values(
					#{ctscateno}
				  , #{ctsno}
				  , ctsdat_next(#{ctscateno},#{ctsno})
				  , #{mno}
				  , #{ctt}
					)

mapper.update:
update ctsdat
   set ctt = #{ctt}
 where ctscateno = #{ctscateno}
   and ctsno = #{ctsno}
   and ctsdatno = #{ctsdatno}
   and mno = #{mno}

mapper.delete:
delete
  from ctsdat
 where ctscateno = #{ctscateno}
   and ctsno = #{ctsno}
   and ctsdatno = #{ctsdatno}
   and mno = #{mno}
