mapper.select:
select * 
from mem 
where siteno = #{siteno} and siteid = #{siteid}

select count(niknm)
  from mem
 where niknm = #{niknm}

mapper.update:
update vw_mem set email=#{email}, pic=#{pic}, license=#{license}, msgrcyn=#{license}, prmsg=#{prmsg},
		locno=#{locno}, locnm=#{locnm}, mdrnm=#{mdrnm}, actnm=#{actnm}
where mno = #{mno}

update bsubj set
	     subjttl = #{subjttl}
	   , subjctt = #{subjctt}
	   , delyn = #{delyn}
 where subjno= #{subjno}

update act set
		 actstdt = #{actstdt}
	   , acteddt = #{acteddt}
	   , actnm = #{actnm}
	   , actctt = #{actctt}
	   , actgb = #{actgb}
	   , pryn = #{pryn}
 where mno = #{mno} and actno = #{actno}

update drone
   set  model = #{model}
	  , mdrnm = #{mdrnm}
	  , pryn = #{pryn}
 where mdrno =#{mdrno}
