mapper.select:
select * 
from mem 
where siteno = #{siteno} and siteid = #{siteid}

select niknm
  from mem
 where niknm = #{niknm}

mapper.update:
update vw_mem set email=#{email}, pic=#{pic}, license=#{license}, msgrcyn=#{license}, prmsg=#{prmsg},
		locno=#{locno}, locnm=#{locnm}, mdrnm=#{mdrnm}, actnm=#{actnm}
where mno = #{mno}
